cy.request({
  method: 'POST',
  url: `${Cypress.env('apiUrl')}/login`,
  body: {
    email: _constants.USER_ADMIN.email,
    password: _constants.USER_ADMIN.password
  }
}).then(response => {
  // Assertivas usando Chai
  expect(response.status).to.equal(200);
  expect(response.body).to.have.property('message', 'Login realizado com sucesso');
  expect(response.body).to.have.property('authorization');
  expect(response.body.authorization).to.not.be.empty;
  // Opcional: Salvar o token para uso em outros testes (abordaremos isso no próximo passo)
  Cypress.env('authToken', response.body.authorization);
});

cy.request({
  method: 'POST',
  url: `${Cypress.env('apiUrl')}/login`,
  failOnStatusCode: false,
  // Não falhar o teste se o status code não for 2xx
  body: {
    email: _constants.USER_ADMIN.email,
    password: 'senha_invalida'
  }
}).then(response => {
  // Assertivas usando Chai
  expect(response.status).to.equal(401); // Unauthorized
  expect(response.body).to.have.property('message', 'Email e/ou senha inválidos');
});
